static void _cbBMPButton(WM_MESSAGE * pMsg) {
  GUI_RECT Rect;
  int Index;

  switch (pMsg->MsgId) {
  case WM_PAINT:
    WM_GetClientRectEx(pMsg->hWin, &Rect);
    if(stateONOFF){
      GUI_DrawBitmap(&bmp_button_green, 0, 0);
    } else {
      GUI_DrawBitmap(&bmp_button_red, 0, 0);
    }
    break;
  default:
    BUTTON_Callback(pMsg); // The original callback
    break;
  }
}

